mapper.select:
SELECT * 
FROM DDOKUSER
WHERE EMAIL = #{email} AND PASSWORD = #{password}

mapper.update:
UPDATE DDOKUSER 
SET USERIMG = #{userImg}, NICKNAME = #{nickname}, PHONE = #{phone}
WHERE USERIDX = #{userIdx}

UPDATE DDOKUSER 
SET PASSWORD = #{password}
WHERE USERIDX = #{userIdx}

UPDATE DDOKUSER 
SET PASSWORD = #{password}
WHERE NAME = #{name} AND EMAIL = #{email}

